import React, { useState } from 'react';
import { askTheCosmos } from '../api/ask';

const AskTheCosmos = () => {
  const [input, setInput] = useState('');
  const [chat, setChat] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleAsk = async () => {
    if (!input.trim()) return;
    const userMsg = input;
    setInput('');
    setChat(prev => [...prev, { role: 'user', text: userMsg }]);
    setLoading(true);

    try {
      const reply = await askTheCosmos(userMsg);
      setChat(prev => [...prev, { role: 'assistant', text: reply }]);
    } catch {
      setChat(prev => [...prev, { role: 'assistant', text: 'Something went wrong!' }]);
    }

    setLoading(false);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">ðŸ¤– Ask the Cosmos</h2>
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          className="w-full border px-3 py-2 rounded"
          placeholder="Ask me anything about space..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleAsk()}
        />
        <button
          className="bg-purple-600 text-white px-4 py-2 rounded"
          onClick={handleAsk}
        >
          Ask
        </button>
      </div>

      <div className="space-y-4">
        {chat.map((msg, i) => (
          <div key={i} className={`p-3 rounded ${msg.role === 'user' ? 'bg-gray-100' : 'bg-green-100'}`}>
            <strong>{msg.role === 'user' ? 'You' : 'Cosmos'}:</strong> {msg.text}
          </div>
        ))}
        {loading && <p>Thinking...</p>}
      </div>
    </div>
  );
};

export default AskTheCosmos;
