import React, { useState } from 'react';
import { fetchMarsPhotos } from '../api/mars';

const MarsExplorer = () => {
  const [photos, setPhotos] = useState([]);
  const [date, setDate] = useState('');
  const [camera, setCamera] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const searchPhotos = async () => {
    setLoading(true);
    setError('');
    try {
      const data = await fetchMarsPhotos({ date, camera });
      setPhotos(data);
    } catch (err) {
      setError('Failed to load Mars photos');
    }
    setLoading(false);
  };

  return (
    <section className="container bg-dark text-light p-5 rounded mb-5" id="mars">
      <h2 className="display-6 fw-bold text-warning mb-4">ðŸš— Mars Rover Photo Explorer</h2>

      {/* Input Controls */}
      <div className="row g-3 mb-4 align-items-end">
        <div className="col-md-4">
          <label className="form-label">Select Date</label>
          <input
            type="date"
            className="form-control bg-black text-white border-secondary"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>

        <div className="col-md-4">
          <label className="form-label">Select Camera</label>
          <select
            className="form-select bg-black text-white border-secondary"
            value={camera}
            onChange={(e) => setCamera(e.target.value)}
          >
            <option value="">All Cameras</option>
            <option value="fhaz">Front Hazard</option>
            <option value="rhaz">Rear Hazard</option>
            <option value="navcam">Navigation</option>
          </select>
        </div>

        <div className="col-md-4 text-end">
          <button className="btn btn-outline-light px-4 py-2 rounded" onClick={searchPhotos}>
            Search
          </button>
        </div>
      </div>

      {/* Loader/Error */}
      {loading && <p>ðŸ”„ Loading photos...</p>}
      {error && <p className="text-danger">{error}</p>}

      {/* Photo Cards */}
      {photos.length > 0 ? (
        <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-4">
          {photos.map((photo, idx) => (
            <div className="col" key={idx}>
              <div className="card h-100 bg-secondary text-white shadow">
                <img
                  src={photo.img_src}
                  alt={`Mars Rover - ${photo.camera.full_name}`}
                  className="card-img-top"
                />
                <div className="card-body">
                  <h5 className="card-title">{photo.camera.full_name}</h5>
                  <p className="card-text small text-light">Rover: {photo.rover.name}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        !loading && <p className="text-muted mt-4">No photos found for the selected date and camera.</p>
      )}
    </section>
  );
};

export default MarsExplorer;
