import React, { useState } from 'react';
import { fetchMarsPhotos } from '../api/mars';

const MarsExplorer = () => {
  const [photos, setPhotos] = useState([]);
  const [date, setDate] = useState('');
  const [camera, setCamera] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const searchPhotos = async () => {
    setLoading(true);
    setError('');
    try {
      const data = await fetchMarsPhotos({ date, camera });
      setPhotos(data);
    } catch (err) {
      setError('Failed to load Mars photos');
    }
    setLoading(false);
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">ðŸš— Mars Rover Photo Explorer</h2>
      <div className="flex gap-4 mb-4">
        <input
          type="date"
          className="border px-3 py-2 rounded"
          value={date}
          onChange={e => setDate(e.target.value)}
        />
        <select
          className="border px-3 py-2 rounded"
          value={camera}
          onChange={e => setCamera(e.target.value)}
        >
          <option value="">All Cameras</option>
          <option value="fhaz">Front Hazard</option>
          <option value="rhaz">Rear Hazard</option>
          <option value="navcam">Navigation</option>
        </select>
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={searchPhotos}
        >
          Search
        </button>
      </div>

      {loading && <p>Loading photos...</p>}
      {error && <p className="text-red-500">{error}</p>}

      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {photos.map(photo => (
          <div key={photo.id} className="shadow p-2 rounded border">
            <img src={photo.img_src} alt="Mars" className="w-full rounded" />
            <p className="text-sm mt-2">{photo.camera.full_name}</p>
          </div>
        ))}
      </div>

      {photos.length === 0 && !loading && <p>No photos found.</p>}
    </div>
  );
};

export default MarsExplorer;
