import React, { useState } from 'react';
import { fetchNEOData } from '../api/neo';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Tooltip } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip);

const NeoDashboard = () => {
  const [start, setStart] = useState('');
  const [end, setEnd] = useState('');
  const [neoData, setNeoData] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadData = async () => {
    setLoading(true);
    const data = await fetchNEOData(start, end);
    setNeoData(data);
    setLoading(false);
    console.log("fetchNEOData:", fetchNEOData);
  };
  console.log(neoData)

  const chartData = {
    labels: neoData.map(d => d.name),
    datasets: [
      {
        label: 'Velocity (km/h)',
        data: neoData.map(d => d.velocity_kmh),
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      },
    ],
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">☄️ Near Earth Object Dashboard</h2>

      <div className="flex gap-4 mb-4">
        <input
          type="date"
          className="border px-3 py-2 rounded"
          value={start}
          onChange={e => setStart(e.target.value)}
        />
        <input
          type="date"
          className="border px-3 py-2 rounded"
          value={end}
          onChange={e => setEnd(e.target.value)}
        />
        <button
          className="bg-green-600 text-white px-4 py-2 rounded"
          onClick={loadData}
        >
          Load NEO Data
        </button>
      </div>

      {loading && <p>Loading...</p>}

      {neoData.length > 0 && (
        <Bar data={chartData} options={{ responsive: true, plugins: { tooltip: { enabled: true } } }} />
      )}

      {neoData.length === 0 && !loading && <p>No data loaded yet.</p>}
    </div>
  );
};

export default NeoDashboard;
