import React, { useState } from 'react';
import { searchNASAImages } from '../api/search';

const NasaImageSearch = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!query) return;
    setLoading(true);
    try {
      const data = await searchNASAImages(query);
      setResults(data);
    } catch (err) {
      console.error("Search failed", err);
    }
    setLoading(false);
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">ðŸ”Ž NASA Image Search</h2>

      <div className="flex gap-4 mb-6">
        <input
          type="text"
          placeholder="Search for a planet, galaxy, etc."
          value={query}
          onChange={e => setQuery(e.target.value)}
          className="border px-3 py-2 w-full rounded"
        />
        <button onClick={handleSearch} className="bg-blue-600 text-white px-4 py-2 rounded">
          Search
        </button>
      </div>

      {loading && <p>Loading...</p>}

      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {results.map((item, idx) => (
          <div key={idx} className="border p-2 rounded shadow-sm">
            <img src={item.imageUrl} alt={item.title} className="w-full rounded" />
            <h3 className="text-md font-semibold mt-2">{item.title}</h3>
            <p className="text-sm text-gray-600 line-clamp-3">{item.description}</p>
          </div>
        ))}
      </div>

      {!loading && results.length === 0 && query && (
        <p>No results found.</p>
      )}
    </div>
  );
};

export default NasaImageSearch;
